//BLSolution
var http = require('http')  
var bl = require('bl')  

var out1 = ""
var out2 = ""
var out3 = ""
var done = 0;

function endCallback() {
 done++;
 if(done == 3)
 {
  console.log(out1)
  console.log(out2)
  console.log(out3)
 }
}
http.get(process.argv[2], function (response) {  
  response.pipe(bl(function (err, data) {  
    if (err)  
      return console.error(err)  
    out1 = data.toString() 
    endCallback();
  }))
})  
http.get(process.argv[3], function (response) {  
  response.pipe(bl(function (err, data) {  
    if (err)  
      return console.error(err)  
    out2 = data.toString()  
    endCallback();
  }))
})  
http.get(process.argv[4], function (response) {  
  response.pipe(bl(function (err, data) {  
    if (err)  
      return console.error(err)  
    out3 = data.toString()  
    endCallback()
  }))//.on('end', endCallback) 
})  